import React, {useState} from "react";
import {Redirect, Link } from 'react-router-dom';
import {Card, Form, Input, Button, Error} from '../auth/AuthForms';
import io from "socket.io-client";
import {useAuth} from "../../context/AuthContext";

function Signup(props) {
    const referer = (props.location.state != null) ? props.location.state.referer : '/';
    const socket = io();
    const auth = useAuth();

    const [isError, setIsError] = useState(false);
    const [username, setUserName] = useState("");
    const [password, setPassword] = useState("");
    const [password_confirm, setPasswordConfirm] = useState("");

    //Fonction pour envoyer les infos pour l'inscription
    function postSignup() {
        //Vérification que les deux mots de passe correspondent
        if (password === password_confirm) {
            //Émission d'un message avec les infos
            socket.emit("signup", username, password);
            //Lors de la réception d'un message de succès d'authentification
            socket.on("signup_info", function (success) {
                if (success) {
                    auth.setUsername(username);
                    auth.setPassword(password);
                    auth.setAuthenticated(true);
                }
                else setIsError(true);
            });
        }
        else
            //Détection d'un problème
            setIsError(true);
        return false;
    }

    if (auth.authenticated) {
        return <Redirect to={referer} />;
    }

    return (
        <Card>
            <Form>
                <Input
                    type="username"
                    value={username}
                    onChange={e => {
                        setUserName(e.target.value);
                    }}
                    placeholder="Identifiant"
                />
                <Input
                    type="password"
                    value={password}
                    onChange={e => {
                        setPassword(e.target.value);
                    }}
                    placeholder="Mot de passe"
                />
                <Input
                    type="password"
                    value={password_confirm}
                    onChange={e => {
                        setPasswordConfirm(e.target.value);
                    }}
                    placeholder="Confirmation"
                />
                <Button onClick={postSignup}>Sign Up</Button>
            </Form>
            <Link to="/login">Vous avez déjà un comte ?</Link>
            { isError && <Error>Erreur lors de l'inscription.</Error> }
        </Card>
    );
}

export default Signup;
