import React, {useState} from "react";
import { Link } from "react-router-dom";
import { Card, Form, Input, Button, Error } from "../auth/AuthForms";
import {Redirect} from "react-router-dom";
import io from "socket.io-client";
import {useAuth} from "../../context/AuthContext";

function Login(props) {
    const referer = (props.location.state != null) ? props.location.state.referer : '/';
    const socket = io();
    const auth = useAuth();
    const [isError, setIsError] = useState(false);

    function postLogin() {
        socket.emit("login", auth.username, auth.password);
        socket.on("auth_info", function(success) {
            if (success) {
                auth.setAuthenticated(true);
            }
            else {
                auth.setAuthenticated(false);
                setIsError(true);
            }
        });
        return false;
    }

    if (auth.authenticated) {
        return <Redirect to={referer} />;
    }

    return (
        <Card>
            <Form>
                <Input
                    type="text"
                    onChange={e => {
                        auth.setUsername(e.target.value);
                    }}
                    placeholder="Identifiant"
                />
                <Input
                    type="password"
                    onChange={e => {
                        auth.setPassword(e.target.value);
                    }}
                    placeholder="Mot de passe"
                />
                <Button onClick={postLogin}>Sign In</Button>
            </Form>
            <Link to="/signup">Vous n'avez pas de compte ?</Link>
            { isError &&<Error>The username or password provided were incorrect!</Error> }
        </Card>
    );
}

export default Login;
