import React, {useState} from "react";
import {Redirect, Link } from 'react-router-dom';
import {Card, Form, Input, Button, Error} from '../auth/AuthForms';
import {useAuth} from "../../context/auth";
import io from "socket.io-client";

function Signup() {
    const socket = io();

    const [isLoggedIn, setLoggedIn] = useState(false);
    const [isError, setIsError] = useState(false);
    const [username, setUserName] = useState("");
    const [password, setPassword] = useState("");
    const [password_confirm, setPasswordConfirm] = useState("");
    const { setAuthTokens } = useAuth();

    //Fonction pour envoyer les infos pour l'inscription
    function postSignup() {
        //Vérification que les deux mots de passe correspondent
        if (password === password_confirm)
            //Émission d'un message avec les infos
            socket.emit("signup", username, password);
        else
            //Détection d'un problème
            setIsError(true);
        return false;
    }

    //Lors de la réception d'un message de succès d'authentification
    socket.on("auth_success", function () {
        setAuthTokens();
        //Redirection vers l'accueil
        return <Redirect to="/" />;
    });

    return (
        <Card>
            <Form>
                <Input
                    type="username"
                    value={username}
                    onChange={e => {
                        setUserName(e.target.value);
                    }}
                    placeholder="Identifiant"
                />
                <Input
                    type="password"
                    value={password}
                    onChange={e => {
                        setPassword(e.target.value);
                    }}
                    placeholder="Mot de passe"
                />
                <Input
                    type="password"
                    value={password_confirm}
                    onChange={e => {
                        setPasswordConfirm(e.target.value);
                    }}
                    placeholder="Confirmation"
                />
                <Button onClick={postSignup}>Sign Up</Button>
            </Form>
            <Link to="/login">Vous avez déjà un comte ?</Link>
            { isError && <Error>Erreur lors de l'inscription.</Error> }
        </Card>
    );
}

export default Signup;
