import React from "react";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
    Redirect,
    useHistory,
    useLocation
} from "react-router-dom";
import {Navbar, Nav, Button} from "react-bootstrap";
import "./App.css";

export default function App() {
    return (
        <Router>
            <div>
                <Navbar bg="dark" variant="dark">
                    <Navbar.Brand>
                        <Link class="navbar-brand" to="/">Gambling</Link>
                    </Navbar.Brand>
                    <NavLinks />
                    <Navbar.Collapse class="justify-content-end">
                        <Navbar.Text>
                            <AuthButton />
                        </Navbar.Text>
                    </Navbar.Collapse>
                </Navbar>

                <Switch>
                    <Route path="/login">
                        <LoginPage />
                    </Route>
                    <PrivateRoute path="/gambling">
                        <ProtectedPage />
                    </PrivateRoute>
                </Switch>
            </div>
        </Router>
    );
}

const fakeAuth = {
    isAuthenticated: false,
    authenticate(cb) {
        fakeAuth.isAuthenticated = true;
        setTimeout(cb, 100); // fake async
    },
    signout(cb) {
        fakeAuth.isAuthenticated = false;
        setTimeout(cb, 100);
    }
};

function NavLinks() {
    let history = useHistory();

    return fakeAuth.isAuthenticated ? (
        <Nav className="mr-auto">
            <Link class="nav-link" to="/gambling">Play</Link>
        </Nav>
    ) : (
        <Nav className="mr-auto">
            <Link class="nav-link" to="/login">Login</Link>
        </Nav>
    );
}

function AuthButton() {
    let history = useHistory();

    return fakeAuth.isAuthenticated ? (
        <p>
            Welcome!{" "}
            <Button onClick={() => {fakeAuth.signout(() => history.push("/"));}}>
                Sign out
            </Button>
        </p>
    ) : (
        <p>You are not logged in.</p>
    );
}

function PrivateRoute({children}) {
    return (
        <Route
            render={({location}) =>
                fakeAuth.isAuthenticated ? (
                    children
                ) : (
                    <Redirect
                        to={{
                            pathname: "/login",
                            state: { from: location }
                        }}
                    />
                )
            }
        />
    );
}

function ProtectedPage() {
    return <h3>You can play !</h3>;
}

function LoginPage() {
    let history = useHistory();
    let location = useLocation();

    let { from } = location.state || { from: { pathname: "/" } };
    let login = () => {
        fakeAuth.authenticate(() => {
            history.replace(from);
        });
    };

    return (
        <div>
            <p>You need to login to play !</p>
            <Button onClick={login}>Log in</Button>
        </div>
    );
}
