import React, {useState} from "react";
import { Link } from "react-router-dom";
import {Redirect} from "react-router-dom";
import io from "socket.io-client";
import {useAuth} from "../../context/AuthContext";
import {Form, Button, Card} from "react-bootstrap";

function Login(props) {
    const referer = (props.location.state != null) ? props.location.state.referer : '/';
    const socket = io();
    const auth = useAuth();
    const [isError, setIsError] = useState(false);

    function postLogin() {
        socket.emit("login", auth.username, auth.password);
        socket.on("auth_info", function(success) {
            if (success) {
                auth.setAuthenticated(true);
            }
            else {
                auth.setAuthenticated(false);
                setIsError(true);
            }
        });
        return false;
    }

    if (auth.authenticated) {
        return <Redirect to={referer} />;
    }

    return (
        <Card style={{ width: '20rem' }}>
            <Form>
                <Form.Group controlId="formBasicEmail">
                    <Form.Control
                        type="text"
                        onChange={e => {
                            auth.setUsername(e.target.value);
                        }}
                        placeholder="Identifiant"
                    />
                </Form.Group>

                <Form.Group controlId="formBasicPassword">
                    <Form.Control
                        type="password"
                        onChange={e => {
                            auth.setPassword(e.target.value);
                        }}
                        placeholder="Mot de passe"
                    />
                </Form.Group>
                <Button variant="primary" onClick={postLogin}>
                    Connexion
                </Button>
            </Form>
            <Link to="/signup">Vous n'avez pas de compte ?</Link>
            { isError &&<h3 color={"danger"}>Identifiant / Mot de passe incorrect</h3> }
        </Card>
    );
}

export default Login;
