import React from "react";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link,
} from "react-router-dom";
import {Navbar, Nav, Button} from "react-bootstrap";
import "./App.css";
import { AuthContext } from "./context/auth";
import PrivateRoute from "./components/routing/PrivateRoute";
import Home from "./components/pages/Home";
import Roulette from "./components/pages/Roulette";
import Crash from "./components/pages/Crash";
import Jackpot from "./components/pages/Jackpot";
import { useAuth } from "./context/auth";
import Login from "./components/pages/Login";
import Signup from './components/pages/Signup';
import { useHistory } from "react-router-dom";
import { useState } from "react";

export default function App() {
    const existingTokens = JSON.parse(localStorage.getItem("tokens"));
    const [authTokens, setAuthTokens] = useState(existingTokens);

    const setTokens = (data) => {
        localStorage.setItem("tokens", JSON.stringify(data));
        setAuthTokens(data);
    };

    return (
        <AuthContext.Provider value={false}>
            <Router>
                <Navbar bg="dark" variant="dark">
                    <Navbar.Brand>
                        <Link class="navbar-brand" to="/">Casino</Link>
                    </Navbar.Brand>
                    <NavLinks />
                    <Navbar.Collapse class="justify-content-end">
                        <Navbar.Text>
                            <AuthButton />
                        </Navbar.Text>
                    </Navbar.Collapse>
                </Navbar>
                <Switch>
                    <Route exact path="/" component={Home}/>
                    <Route path="/login" component={Login}/>
                    <Route path="/signup" component={Signup}/>
                    <PrivateRoute path="/roulette" component={Roulette}/>
                    <PrivateRoute path="/crash" component={Crash}/>
                    <PrivateRoute path="/jackpot" component={Jackpot}/>
                </Switch>
            </Router>
        </AuthContext.Provider>
    );
}

function NavLinks() {
    const isAuthenticated = useAuth();

    return isAuthenticated ? (
        <Nav className="mr-auto">
            <Link class="nav-link" to="/roulette">Roulette</Link>
            <Link class="nav-link" to="/crash">Crash</Link>
            <Link class="nav-link" to="/jackpot">Jackpot</Link>
        </Nav>
    ) : (
        <Nav className="mr-auto"/>
    );
}

function AuthButton() {
    const isAuthenticated = useAuth();
    const { setAuthTokens } = useAuth();
    const history = useHistory();

    function handleClick(e) {
        e.preventDefault();
        if(isAuthenticated)
            setAuthTokens();
        else
            history.push("/login");
    }

    return isAuthenticated ? (
        <Button variant="outline-primary" onClick={handleClick}>Déconnexion</Button>
    ) : (
        <Button variant="outline-primary" onClick={handleClick}>Connexion</Button>
    );
}
